import {useState, useEffect} from "react";
import CookieTable from "./CookieTable";
import axios from "axios";

// should be like cookie info
function AddCookie() {
    const [cookies , setNewCookies] = useState(null)
    const [formCookie, setFormCookie] = useState({ // form cookie data
            name: '',
            rarity: '',
            attack: 0,
            defense: 0,
            health: 0,
            description: "",
          })

    // so getCookies runs right after render is displayed
    useEffect(() => {
    getCookies()
        } ,[])

    // get cookies info from database
    function getCookies() {
    axios({
        method: "GET",
        url:"/api/cookies/",
        // '/api/cookies/?format=json'
        }).then((response)=>{ // success
            const data = response.data
            setNewCookies(data)
            console.log(response)
            console.log(data)
            console.log(data.data)
            console.log(data[data])
            console.log(data[0])
            console.log(cookies)

        }).catch((error) => { // failure
        if (error.response) {
            console.log(error.response);
            console.log(error.response.status);
            console.log(error.response.headers);
            }
        })}

    // send new cookies info to database
    function createCookie(event) {
        axios({
            method: "POST",
            url:"/api/cookies",
            data:{
                name: formCookie.name,
                rarity: formCookie.rarity,
                attack: formCookie.attack,
                defense: formCookie.defense,
                health: formCookie.health,
                description: formCookie.description,
            }
        })

        .then((response) => { // success
            getCookies()
        })

        // reset form input
        setFormCookie(({
            name: '',
            rarity: '',
            attack: 0,
            defense: 0,
            health: 0,
            description: "",}))

        // prevents page reload
        event.preventDefault()
    }

    // delete cookie based on id
    function DeleteCookie(id) {
        axios({
          method: "DELETE",
          url:`/api/cookies/${id}/`,
        })
        .then((response) => {
          getCookies()
        });
    }

    function handleChange(event) { 
        const {value, name} = event.target
        setFormCookie(prevCookie => ({
            ...prevCookie, [name]: value})
    )}



    return(
        <div className=''>
            {/* ADD COOKIE FORM */}
